'][\'socid\'];__N__    $contact_id = GETPOST(\'contact\');   // TODO This should come from the session__N____N__    $error = 0;__N__    $error_messages = array();__N__	__N__	if (empty($customer_firstname)) {__N__		$error++;__N__		$error_messages[] = $weblangs->trans(\"fieldRequired\", $weblangs->trans(\"firstname\"));__N__	}__N__	__N__	//if (ctype_alpha(str_replace(array(\"\\n\", \"\\t\", \' \'), \'\', $customer_firstname)) === false) {__N__	if (!preg_match(\'/^[a-zA-ZÃ-ÃÃ-Ã¶Ã¸-Ã¿\\- ]+$/u\', $customer_firstname)) {__N__	    $error++;__N__        $error_messages[] = $weblangs->trans(\"firstnameContainsLettersOnly\");__N__    }__N__	__N__	if (empty($customer_lastname)) {__N__		$error++;__N__		$error_messages[] = $weblangs->trans(\"fieldRequired\", $weblangs->trans(\"lastname\"));__N__	}__N__	__N__	//if (ctype_alpha(str_replace(array(\"\\n\", \"\\t\", \' \'), \'\', $customer_lastname)) === false) {__N__	if (!preg_match(\'/^[a-zA-ZÃ-ÃÃ-Ã¶Ã¸-Ã¿\\- ]+$/u\', $customer_lastname)) {__N__	    $error++;__N__        $error_messages[] = $weblangs->trans(\"lastnameContainsLettersOnly\");__N__    }__N__	__N__	if (!empty($customer_passwd) && !empty($customer_new_passwd)) {__N__	    $id_website = $website->id;__N__        __N__        $sql  = \"select lsa.rowid, lsa.pass_encoding, lsa.pass_crypted, ls.nom, fk_soc \";__N__        $sql .= \"from \".MAIN_DB_PREFIX.\"societe_account lsa \";__N__        $sql .= \"left join \".MAIN_DB_PREFIX.\"societe ls on ls.rowid = lsa.fk_soc \";__N__        $sql .= \"where lsa.login = \'\". $customer_email .\"\' \";__N__        $sql .= \"and lsa.fk_website = \". $id_website . \" \";__N__        // TODO The where must be WHERE lsa.rowid = ((int) $_SESSION[\'user\'][\'id\'])__N__        $sql .= \"limit 1\";__N__        __N__        $result = $db->query($sql);__N__		$objsql = $db->fetch_object($result);__N__        if (!empty($objsql)) {__N__    		if ($objsql->pass_encoding == \'dolibarr\') {__N__    			$correct_password = dol_verifyHash ($custome