                    reader.onload = function(e) {__N__                        imagePreview.attr(\'src\', e.target.result).css(\'display\', \'block\');__N__                    };__N__    __N__                    reader.readAsDataURL(this.files[0]);__N__                } else {__N__                    imagePreview.attr(\'src\', \'\').css(\'display\', \'none\');__N__                }__N__            });__N__        });__N__    __N__        // Delete row functionality__N__        tableBody.on(\'click\', \'.deleteBtn\', function() {__N__            $(this).closest(\'tr\').remove();__N__            updateRowNumbers();__N__        });__N__    __N__        // Function to update row numbers and input names__N__        function updateRowNumbers() {__N__            tableBody.find(\'tr\').each(function(index) {__N__                var rowNum = index + 1;__N__                $(this).find(\'.rowCount\').text(rowNum);__N__                $(this).find(\'.image-input\').attr(\'name\', `image_${rowNum}`);__N__            });__N__        }__N__    __N__        // Make table rows sortable add__N__        $(\'#table-body-add\').sortable({__N__            handle: \'td:first-child\', // Restrict drag start to the first cell (Position)__N__            update: function(event, ui) {__N__                updateRowNumbers(); // Update row numbers and input names after reordering__N__            },__N__            start: function(event, ui) {__N__              ui.item.addClass(\'dragging\');__N__            },__N__            stop: function(event, ui) {__N__              ui.item.removeClass(\'dragging\');__N__            },__N__        });__N__        __N__        document.getElementById(\'image_cover\').addEventListener(\'change\', function(event) {__N__            const file = event.target.files[0];__N__            if (file) {__N__                const reader = new FileReader();__N__                reader.onload = function(e) {__N__                    const img = document.getElementById(\'image_preview\');__N__                    img.src = e.target.result;__N__                    img.style.display = \'block\';__N__                }__N__                reader.readAsDataURL(file);__N__            }__N__        });__N__        __N__        document.getElementById(\'virtual_product_file\').addEventListener(\'change\', function(event) {__N__            var fileInfoDiv = document.getElementById(\'field-info-virtual_product_file\');__N__            var fileName = event.target.files[0] ? event.target.files[0].name : \"Aucun fichier sÃ©lectionnÃ©\";__N__            fileInfoDiv.textContent = fileName;__N__        });__N__        document.getElementById(\'image_cover\').addEventListener(\'change\', function(event) {__N__            var fileInfoDiv = document.getElementById(\'field-info-image-cover\');__N__            var fileName = event.target.files[0] ? event.target.files[0].name : \"Aucun fichier sÃ©lectionnÃ©\";__N__            fileInfoDiv.textContent = fileName;__N__        });__N__    __N__        // Make table rows sortable edit__N__        $(\'#table-body-edit\').sortable({__N__            handle: \'td:first-child\',__N__            update: function(event, ui) {__N__                updateEditRowNumbers();__N__        __N__                var order = getOrder();__N__                $.ajax({__N__                    url: edit_product_url,__N__                    type: \'POST\',__N__                    dataType: \'json\',__N__                    data: { action: \'update_order\', order: order },__N__                    success: function(response) {__N__                        showNotification(response.message);__N__                        if (response.success) {__N__                            // Optionnel : mettre Ã  jour l\'interface utilisateur__N__                        }__N__                    },__N__                    fail: function(response) {__N__                        alert(response.message);__N__                    }__N__                });__N__            },__N__            start: function(event, ui) {__N__                ui.item.addClass(\'dragging\');__N__            },__N__            stop: function(event, ui) {__N__                ui.item.removeClass(\'dragging\');__N__            },__N__        });__N__    __N__        function getOrder() {__N__            var order = {};__N__            $(\'#table-body-edit tr\').each(function(index, element) {__N__                var id = $(element).find(\'.deleteBtn\').data(\'id\');__N__                var position = index + 1;__N__                order[id] = position;__N__            });__N__            return order;__N__        }__N__    __N__    __N__        $(\'#addImageBtn2\').on(\'click\', function() {__N__            $(\'#fileInput2\').click();__N__        });__N__    __N__        $(\'#fileInput2\').on(\'change\', function(e) {__N__            e.preventDefault();__N__            var formData = new FormData();__N__            var position_for_new_photo = $(\'#table-body-edit tr\').length + 1;__N__            var product_id = $(\'#product_to_edit\').text();__N__    __N__            formData.append(\'product_id\', product_id);__N__            formData.append(\'action\', \'add_product_image\');__N__            formData.append(\'position\', position_for_new_photo);__N__            formData.append(\'file\', this.files[0]);__N__            __N__            $.ajax({__N__                url: edit_product_url,__N__                type: \'POST\',__N__                dataType: \'json\',__N__                data: formData,__N__                processData: false,__N__                contentType: false__N__            }).done(function(response) {__N__                showNotification(response.message);__N__                if (response.success) {__N__                    location.reload(); // Recharge la page__N__                }__N__            }).fail(function(response) {__N__                alert(response.message);__N__            });__N__        });__N__    __N__        __N__        $(\'.deleteBtn\').on(\'click\', function(e) {__N__            e.preventDefault();__N__            var id_image = $(this).data(\'id\');__N__            $.ajax({__N__                url: edit_product_url,__N__                type: \"POST\",__N__                dataType: \"json\", // On attend une rÃ©ponse JSON du serveur__N__                data: { action: \'delete_image\', \'id_image\': id_image }__N__            }).done(function(response) {__N__                showNotification(response.message);__N__                if (response.success) {__N__                    $(\'tr[data-id=\"\' + id_image + \'\"]\').remove();__N__                    //updateEditRowNumbers();__N__                }__N__            }).fail(function() {__N__                alert(\'error\');__N__            });__N__        });__N__    __N__    __N__        function updateEditRowNumbers() {__N__            $(\'#table-body-edit tr\').each(function(index) {__N__                $(this).find(\'.rowCount\').text(index + 1);__N__            });__N__        }__N__    __N__        function showNotification(message) {__N__            $(\'.notification\').text(message).fadeIn().delay(2000).fadeOut();__N__        }__N__    __N__    __N__        // Get references to the checkboxes and the submit button__N__        const dolibarrTermsCheckbox = document.getElementById(\'dolibarr_terms\');__N__        const moduleWikiCheckbox = document.getElementById(\'module_wiki\');__N__        const submitButton = document.getElementById(\'sub\');__N__    __N__        // Function to check the state of the checkboxes and update the button state__N__        function updateButtonState() {__N__            if (dolibarrTermsCheckbox.checked && moduleWikiCheckbox.checked) {__N__                submitButton.disabled = false;__N__                submitButton.style.opacity = 1.0;__N__                submitButton.classList.remove(\'button_large_disabled\');__N__                submitButton.classList.add(\'button_large\');__N__            } else {__N__                submitButton.disabled = true;__N__                submitButton.style.opacity = 0.5;__N__                submitButton.classList.add(\'button_large_disabled\');__N__                submitButton.classList.remove(\'button_large\');__N__            }__N__        }__N__    __N__        // Add event listeners to the checkboxes to monitor changes__N__        dolibarrTermsCheckbox.addEventListener(\'change\', updateButtonState);__N__        moduleWikiCheckbox.addEventListener(\'change\', updateButtonState);__N__    __N__        // Initial check to set the button state based on the initial checkbox states__N__        updateButtonState();__N__    __N__    __N__    });__N__</script>', '<?php // BEGIN PHP__N__session_start(); // start session__N__require_once DOL_DOCUMENT_ROOT . \'/categories/class/categorie.class.php\';__N__require_once DOL_DOCUMENT_ROOT . \'/core/lib/categories.lib.php\';__N__require_once DOL_DOCUMENT_ROOT . \'/core/class/html.formcompany.class.php\';__N__require_once DOL_DOCUMENT_ROOT . \'/core/class/doleditor.class.php\';__N__require_once DOL_DOCUMENT_ROOT . \'/societe/class/societeaccount.class.php\';__N____N__error_reporting(E_ALL);__N__ini_set(\'display_errors\', 1);__N____N__/*print \'<pre>\';__N__print_r($conf);__N__print \'</pre>\';__N__exit;*/__N____N__$tag = GETPOST(\'tag\', \'alpha\');__N__$cat_id = GETPOST(\'cat\', \'alpha\');__N__$search = GETPOST(\'search_query\', \'alpha\');__N__$product_id = GETPOST(\'id\', \'alpha\');__N____N__$isAdmin = false;__N____N__$form = new Form($db);__N____N__// If not login redirect to login.__N__if (empty($_SESSION[\'user\'][\'id_user\'])) {__N__    redirectToContainer(\'login-register\');__N__}__N____N__if (!empty($product_id)) {__N__    $product = new Product($db);__N__    $result = $product->fetch($product_id);__N__    if ($result <= 0) {__N__        print \"Product not found from id=\".((int) $product_id);__N__        //redirectToContainer(\'myproducts\');__N__        exit;__N__    } else {__N__        // Get Account ID.__N__        $user_id = $_SESSION[\'user\'][\'id_user\'];__N__        $supplier_account = new SocieteAccount($db);__N__        $result = $supplier_account->fetch($user_id);__N__        $supplier_id = $supplier_account->fk_soc;__N__        __N__        // Get societe ext_ref__N__        $supplier_account_societe = new Societe($db);__N__        $result = $supplier_account_societe->fetch($supplier_id);__N__        $supplier_id_ext = $supplier_account_societe->ref_ext;__N__        //echo $result; exit;__N____N__        // Special check for old products with old syntax c...d...__N__        if (preg_match(\'/c(\\d+)d/\', $product->ref, $matches) && $matches[1] != $supplier_id && $matches[1] != $supplier_id_ext) {__N__            print \"Product not found from c...d... or supplier ref_ext is not matching the id into the product ref c...d...\";__N__            //redirectToContainer(\'myproducts\');__N__            exit;__N__        }__N__    }__N__}__N____N__// END PHP __N__?>__N__<div id=\"page\">__N__    <div class=\"header-container\">__N__        <?php includeContainer(\'header\'); ?>__N__    </div>__N__    <div class=\"columns-container\">__N__        <div id=\"columns\" class=\"container\">__N__            <!--  Breadcrumb  -->__N__            <div class=\"breadcrumb clearfix\">__N__                <a class=\"home\" href=\"index.php\" title=\"Return to Home\"><i class=\"icon-home\"></i></a>__N__                <span class=\"navigation-pipe\">&gt;</span> <a href=\"myaccount.php\" title=\"<?php print $weblangs->trans(\"myAccount\"); ?>\" data-gg=\"\"><?php print $weblangs->trans(\"myAccount\"); ?></a>__N__                <span class=\"navigation-pipe\">&gt;</span> <?php print $weblangs->trans(\"newModuleProduct\"); ?>__N__            </div>__N__            <!--  Breadcrumb  -->__N__            <div id=\"slider_row\" class=\"row\">__N__                <div id=\"top_column\" class=\"center_column col-xs-12 col-sm-12\"></div>__N__            </div>__N__            <div class=\"row\">__N__                <!-- include left sidebar container -->__N__                <?php includeContainer(\'sidebar\'); ?>__N__                <div id=\"center_column\" class=\"center_column col-xs-12 col-sm-9\">__N__                    <div class=\"clearfix\">__N__                        <script type=\"text/javascript\">__N__                            var edit_product_url = \"_service_page_ajax.php\";__N__                            console.log(edit_product_url);__N__                        </script>__N__                        <h1 class=\"page-heading\"><?php print $weblangs->trans(\"newModuleProduct\"); ?></h1>__N__                        <div class=\"row\">__N__                            <div class=\"col-xs-12\">__N__                                <div class=\"add-product-page\">__N__                                    <?php__N__                                    $website_available_langs = getAvailableLangs();__N__                                    if (!defined(\'ROWS_3\')) {__N__                                        define(\'ROWS_3\', 3);__N__                                    }__N__                                    __N__                                    $mvcid = getDolGlobalInt(\"MARKETPLACE_VERSIONS_CATEGORY_ID\");__N__                                    $full_version_tree = getOrganizedTree($mvcid, \'label\');__N____N__                                    if (!empty($product_id) && $_SERVER[\'REQUEST_METHOD\'] != \'POST\') {__N____N__                                        foreach ($website_available_langs[\'langs_code_shortcode\'] as $lang_key => $code) {__N__                                            ${\'name_\' . $lang_key} = $product->multilangs[$code][\'label\'] ?? \'\';__N__                                            ${\'short_description_\' . $lang_key} = $product->multilangs[$code][\'description\'] ?? \'\';__N__                                            ${\'description_\' . $lang_key} = $product->multilangs[$code][\'other\'] ?? \'\';__N__                                        }__N__                                        $status = $product->status ?? 0;__N__                                        $sale_price = ($product->price ? price($product->price) : \'\');__N____N__                                        $c = new Categorie($db);__N__                                        $cats = $c->containing($product->id, Categorie::TYPE_PRODUCT);__N__                                        $arrayselected = array();__N__                                        if (is_array($cats)) {__N__                                            foreach ($cats as $cat) {__N__                                                $arrayselected[] = $cat->id;__N__                                            }__N__                                        }__N__                                        $categories = array();__N__                                        foreach ($arrayselected as $key => $value) {__N__                                            if (!in_array($value, [\'PRODUCTS_ON_WEBSITE\', \'VERSIONS\']) && strpos($value, \'V\') !== 0) {__N__                                                $categories[$key] = $value;__N__                                            }__N__                                        }__N____N__                                        $version = ($product->array_options[\'options_marketplace_module_version\'] ? $product->array_options[\'options_marketplace_module_version\'] : 0);__N__                                        $keywords = ($product->array_options[\'options_marketplace_module_keywords\'] ? $product->array_options[\'options_marketplace_module_keywords\'] : 0);__N__                                        $dolibarr_core_include = ($product->array_options[\'options_marketplace_allow_source_in_core\'] ? $product->array_options[\'options_marketplace_allow_source_in_core\'] : 0);__N__                                        $version_min = ($product->array_options[\'options_marketplace_min_version\'] ? $product->array_options[\'options_marketplace_min_version\'] : 0);__N__                                        $version_max = ($product->array_options[\'options_marketplace_max_version\'] ? $product->array_options[\'options_marketplace_max_version\'] : 0);__N__                                        $contact_support = ($product->array_options[\'options_marketplace_contact_support\'] ? $product->array_options[\'options_marketplace_contact_support\'] : 0);__N____N__                                        $files_and_images = getPublicFilesOfObject($product);__N__                                        foreach ($files_and_images as $key => $file) {__N__                                            if ($file[\'position\'] == 100) {__N__                                                $virtual_product_file = $file;__N__                                            }__N__                                            if ($file[\'position\'] == 0) {__N__                                                $image_cover = $file;__N__                                            }__N__                                            if (strpos($file[\'filename\'], \'image_\') !== false && strpos($file[\'filename\'], \'image_cover\') === false) {__N__                                                $otherimages[$key] = $file;__N__                                            }__N__                                        }__N__                                    }__N____N____N__                                    if (GETPOST(\'action\') == \"new-module\") {__N__                                        foreach ($website_available_langs[\'langs\'] as $lang_key => $lang_label) {__N__                                            ${\'name_\' . $lang_key} = GETPOST(\'name_\' . $lang_key) ?? \'\';__N__                                            ${\'short_description_\' . $lang_key} = GETPOST(\'short_description_\' . $lang_key) ?? \'\';__N__                                            ${\'description_\' . $lang_key} = GETPOST(\'description_\' . $lang_key, \'restricthtmlallowunvalid\') ?? \'\';__N__                                        }__N____N____N__                                        $error = 0;__N__                                        $error_messages = array();__N____N__                                        foreach ($website_available_langs[\'langs\'] as $lang_key => $lang_label) {__N__                                            $product_name = GETPOST(\'name_\' . $lang_key);__N__                                            $short_description = GETPOST(\'short_description_\' . $lang_key);__N__                                            $description = GETPOST(\'description_\' . $lang_key, \'restricthtmlallowunvalid\');__N____N____N__                                            if ($lang_key == \"en\" && ($product_name == \"\" || $short_description == \"\" || $description == \"\")) {__N__                                                $error++;__N__                                                $error_messages[] = $weblangs->trans(\"englishFieldsRequired\");__N__                                            }__N____N__                                            if ($lang_key != \"en\" && $product_name == \"\") {__N__                                                $product_name = (isset($productlang[\'en\'][\'name\']) ? $productlang[\'en\'][\'name\'] : \'\');__N__                                            }__N____N__                                            if ($lang_key != \"en\" && $short_description == \"\") {__N__                                                $short_description = (isset($productlang[\'en\'][\'short_description\']) ? $productlang[\'en\'][\'short_description\'] : \'\');__N__                                            }__N____N__                                            if ($lang_key != \"en\" && $description == \"\") {__N__                                                $description = (isset($productlang[\'en\'][\'description\']) ? $productlang[\'en\'][\'description\'] : \'\');__N__                                            }__N____N__                                            $productlang[$lang_key][\'name\'] = $product_name;__N__                                            $productlang[$lang_key][\'short_description\'] = $short_description;__N__                                            $productlang[$lang_key][\'description\'] = $description;__N____N__                                            $short_descriptionLength = strlen(strip_tags($short_description));__N__                                            if ($short_descriptionLength > 400) {__N__                                                $error++;__N__                                                $error_messages[\'short_description_\' . $lang_key] = $weblangs->trans(\"fieldTooLong\", $weblangs->trans(\"shortDescription\"), 400, $short_descriptionLength);__N__                                            }__N__                                        }__N____N__                                        $keywords = GETPOST(\'keywords\') ?? \'\';__N____N__                                        $categories = GETPOST(\'categories\') ?? \'\';__N__                                        if (empty($categories)) {__N__                                            $error++;__N__                                            $error_messages[\'categories\'] = $weblangs->trans(\"requiredCategory\");__N__                                        }__N____N__                                        if (empty($_FILES[\'virtual_product_file\'][\'name\']) && empty($product_id)) {__N__                                            $error++;__N__                                            $error_messages[\'virtual_product_file\'] = $weblangs->trans(\"zipProductRequired\");__N__                                        } else {__N__                                            if (!empty($_FILES[\'virtual_product_