                              print  \'style=\"display: none;\"\';__N__                                                                                                                                                                } ?> class=\"image-cover-preview\" id=\"image_preview\" width=\"100px\">__N__                                                                </td>__N__                                                            </tr>__N__                                                        </tbody>__N__                                                    </table>__N__                                                    <!--<div class=\"field-info info-inline\" id=\"field-info-image-cover\">__N__                                                        <?php //echo htmlspecialchars($_FILES[\'image_cover\'][\'name\']); __N__                                                        ?>__N__                                                    </div>-->__N__                                                    <?php //endif; __N__                                                    ?>__N__                                                </div>__N__                                            </div>__N____N__                                            <hr>__N____N__                                            <?php if (empty($product_id)) : ?>__N__                                                <div class=\"form-group row required\">__N__                                                    <label class=\"col-xs-3 col-form-label\"><?php print $weblangs->trans(\"otherImages\"); ?></label>__N__                                                    <div class=\"col-xs-9\">__N__                                                        <button type=\"button\" id=\"addImageBtn\" class=\"btn btn-default button\"><?php print $weblangs->trans(\"AddPhoto\"); ?> <span class=\"iconAddImage\"></span></button>__N__                                                        <table class=\"table\">_