nisseur.facture.class.php\';__N____N__include_once DOL_DOCUMENT_ROOT.\'/core/class/html.form.class.php\';__N____N__//error_reporting(E_ALL);__N__//ini_set(\'display_errors\', 1);__N____N__print \'<script nonce=\"\'.getNonce().\'\" type=\"text/javascript\" src=\"\'.DOL_MAIN_URL_ROOT.\'/core/js/lib_head.js.php\"></script>\'.\"\\n\";__N____N__$form = new Form($db);__N____N__$date_start_timestamp = \'\';__N__$date_end_timestamp = \'\';__N__if (!empty(GETPOST(\'datestart\')) || !empty(GETPOST(\'dateend\'))) {__N__    __N__    $date_start = GETPOST(\'datestart\');__N__    $date_end = GETPOST(\'dateend\');__N__    __N__    $date_start_timestamp = dol_mktime(0, 0, 0, GETPOSTINT(\'datestartmonth\'), GETPOSTINT(\'datestartday\'), GETPOSTINT(\'datestartyear\'));__N__    $date_end_timestamp = dol_mktime(0, 0, 0, GETPOSTINT(\'dateendmonth\'), GETPOSTINT(\'dateendday\'), GETPOSTINT(\'dateendyear\'));__N__    __N__    $error = 0;__N__    $error_messages = array();__N__    __N__    if ((empty($date_start) && !empty($date_end)) || (!empty($date_start) && empty($date_end))) {__N__        $error++;__N__        $error_messages[] = \"oneDateEmpty\";__N__    }__N__    __N__    if ($date_end_timestamp < $date_start_timestamp) {__N__        $error++;__N__        $error_messages[] = \"endDateMustBeGreater\";__N__    }__N__    __N__    $filter = \'\';__N__    if ($error == 0 && !empty($date_start) && !empty($date_end)) {__N__        $filter = \" AND c.date_commande BETWEEN \'\".$db->idate($date_start_timestamp).\"\' AND \'\".$db->idate($date_end_timestamp).\"\'\";__N__    }__N__}__N____N____N____N__$weblangs->load(\'companies\');__N____N__// If not login redirect to login.__N__if(empty($_SESSION[\'user\'][\'id_user\'])){__N__    redirectToContainer(\'login-register\');__N__}__N____N____N__// Get Account ID.__N__$user_id = $_SESSION[\'user\'][\'id_user\'];__N__$customer_account = new SocieteAccount($db);__N__$result = $customer_account->fetch($user_id);__N__$customer_id = $customer_account->f