new Societe($db);__N__$result = $customer->fetch($customer_id);__N____N__// Get liste of products__N__$products_ids = array();__N__/*$sql = \'SELECT fk_product, datec FROM \'.MAIN_DB_PREFIX.\"product_fournisseur_price, \'.MAIN_DB_PREFIX.\'categorie_product AS cp WHERE fk_soc = \".((int) $customer_id). \" AND cp.fk_categorie = \'.getDolGlobalInt(\"MARKETPLACE_ROOT_CATEGORY_ID\").\' GROUP BY fk_product ORDER BY datec DESC\";*/__N__$sql = \'SELECT pf.fk_product, MIN(pf.datec) AS first_date__N__        FROM \'.MAIN_DB_PREFIX.\'product_fournisseur_price as pf, \'.MAIN_DB_PREFIX.\'categorie_product AS cp __N__        WHERE pf.fk_soc = \'.$customer_id.\' AND pf.fk_product = cp.fk_product __N__        AND cp.fk_categorie = \'.getDolGlobalInt(\"MARKETPLACE_ROOT_CATEGORY_ID\").\' __N__        GROUP BY fk_product __N__        ORDER BY first_date DESC\';__N____N__if ($result_products = $db->query($sql)) {__N__	while ($product_id = $result_products->fetch_object()) {__N__        $products_ids[] = $product_id->fk_product;__N__	}__N__}__N__$products_ids_str = implode(\',\', $products_ids);__N____N__if (!empty($products_ids_str)) {__N__    $all_sells_period = \"SELECT SUM(d.qty) AS sells \";__N__    $all_sells_period .= \"FROM \".MAIN_DB_PREFIX.\"commande as c, \".MAIN_DB_PREFIX.\"commandedet as d \";__N__    $all_sells_period .= \"WHERE c.rowid = d.fk_commande and d.fk_product IN (\" . $products_ids_str . \") and d.total_ht != 0 and c.fk_statut IN (1,3) and (c.facture = 1 OR c.ref_ext IS NOT NULL) and c.module_source = \'Marketplace\' \";__N__    if (!empty($filter)) {__N__       $all_sells_period .=  $filter;__N__    }__N__    $resql = $db->query($all_sells_period);__N__    $all_sells_period = $db->fetch_object($resql);__N__    __N__    $sum_all_sells_period = \"SELECT SUM(d.total_ht) as total \";__N__    $sum_all_sells_period .= \"FROM \".MAIN_DB_PREFIX.\"commande as c, \".MAIN_DB_PREFIX.\"commandedet as d \";__N__    $sum_all_sells_period .= \"WHERE c.rowid = d.fk_commande and d.fk_product IN (\" . $products_ids_str . \") and c.fk_statut IN (1,3) and (c.facture = 1 || c.ref_ext IS NOT NULL) and c.module_source = \'Marketplace\' \";__N__    if (!empty($filter)) {__N__       $sum_all_sells_period .=  $filter;__N__    }__N__    $resql = $db->query($sum_all_sells_period);__N__    $sum_all_sells_period = $db->fetch_object($resql);__N__    __N__    $sum_all_validated_sells_period = \"SELECT SUM(d.total_ht) as total \";__N__    $sum_all_validated_sells_period .= \"FROM \".MAIN_DB_PREFIX.\"commande as c, \".MAIN_DB_PREFIX.\"commandedet as d \";__N__    $sum_all_validated_sells_period .= \"WHERE c.rowid = d.fk_commande and d.fk_product IN (\" . $products_ids_str . \") and (c.facture = 1 || c.ref_ext IS NOT NULL) and c.module_source = \'Marketplace\' \";__N__    $sum_all_validated_sells_period .= \"AND c.date_commande < DATE_SUB(NOW(), INTERVAL 1 MONTH)\";__N__    if (!empty($filter)) {__N__       $sum_all_validated_sells_period .=  $filter;__N__    }__N__    $resql = $db->query($sum_all_validated_sells_period);__N__    $sum_all_validated_sells_period = $db->fetch_object($resql);__N__    __N__    $sum_all_sells = \"SELECT SUM(d.total_ht) as total \";__N__    $sum_all_sells .= \"FROM \".MAIN_DB_PREFIX.\"commande as c, \".MAIN_DB_PREFIX.\"commandedet as d \";__N__    $sum_all_sells .= \"WHERE c.rowid = d.fk_commande and d.fk_product IN (\" . $products_ids_str . \") and c.fk_statut IN (1,3) and (c.facture = 1 || c.ref_ext IS NOT NULL) and c.module_source = \'Marketplace\' \";__N__    $resql = $db->query($sum_all_sells);__N__    $sum_all_sells = $db->fetch_object($resql);__N__    __N__    $sum_all_validated_sells = \"SELECT SUM(d.total_ht) as total \";__N__    $sum_all_validated_sells .= \"FROM \".MAIN_DB_PREFIX.\"commande as c, \".MAIN_DB_PREFIX.\"commandedet as d \";__N__    $sum_all_validated_sells .= \"WHERE c.rowid = d.fk_commande and d.fk_product IN (\" . $products_ids_str . \") and (c.facture = 1 || c.ref_ext IS NOT NULL) and c.module_source = \'Marketplace\' \";__N__    $sum_all_validated_sells .= \"AND c.date_commande < DATE_SUB(NOW(), INTERVAL 1 MONTH)\";__N__    __N__    $resql = $db->query($sum_all_validated_sells);__N__    $sells_done_validated = $db->fetch_object($resql);__N__    __N__    // Calculate all discounts for this customer products__N__    $TOTAL_DISCOUNTS = 0;__N__    $like_conditions = [];__N__    foreach ($products_ids as $product_id) {__N__        $like_conditions[] = \"d.description LIKE \'\" . $product_id . \"# %\'\";__N__    }__N__    // Join all conditions with OR__N__    $like_clause = implode(\' OR \', $like_conditions);__N__    __N__    $sum_all_discounts = \"SELECT SUM(d.total_ht) as total \";__N__    $sum_all_discounts .= \"FROM \".MAIN_DB_PREFIX.\"commande as c, \".MAIN_DB_PREFIX.\"commandedet as d \";__N__    $sum_all_discounts .= \"WHERE c.rowid = d.fk_commande \";__N__    $sum_all_discounts .= \"AND (\" . $like_clause . \") \";__N__    $sum_all_discounts .= \"AND (c.facture = 1 OR c.ref_ext IS NOT NULL) \";__N__    $sum_all_discounts .= \"AND c.module_source = \'Marketplace\' \";__N__    $resql = $db->query($sum_all_discounts);__N__    $sells_discounts = $db->fetch_object($resql);__N__    $TOTAL_DISCOUNTS = $sells_discounts->total;__N__    __N__    /*=========== Payment History ====================*/__N__    $payment_history = \"SELECT f.rowid, f.ref, f.fk_statut, f.fk_soc, f.datec, f.datef, f.date_closing, f.total_ht , f.total_ttc \";__N__    $payment_history .= \"FROM \".MAIN_DB_PREFIX.\"facture_fourn as f \";__N__    $payment_history .= \"WHERE f.fk_soc = \" . ((int) $customer_id) . \" and f.fk_statut = 2 and f.paye = 1 ORDER BY f.datef DESC\";__N____N__    $alreadyreceived=0;__N__    $dolistoreinvoices=array();__N__    $linesinvoice = array();__N__    if ($result_payment_history = $db->query($payment_history)) {__N____N__        while ($payment = $result_payment_history->fetch_object()) {__N__            $invoice = new FactureFournisseur($db);__N__            $result = $invoice->fetch($payment->rowid);__N__            __N__            // Define lines of invoice__N__            $linesresp = array();__N__            foreach ($invoice->lines as $line) {__N__                    $linesresp[] = array(__N__                            \'id\' => $line->rowid,__N__                            \'type\' => $line->product_type,__N__                            \'desc\' => dol_htmlcleanlastbr($line->desc),__N__                            \'total_net\' => $line->total_ht,__N__                            \'total_vat\' => $line->total_tva,__N__                            \'total\' => $line->total_ttc,__N__                            \'vat_rate\' => $line->tva_tx,__N__                            \'qty\' => $line->qty,__N__                            \'product_ref\' => $line->product_ref,__N__                            \'product_label\' => $line->product_label,__N__                            \'product_desc\' => $line->product_desc,__N__                    );__N__            }__N__            __N__            // Now define invoice__N__            $linesinvoice[] = array(__N__                    \'id\' => $invoice->id,__N__                    \'ref\' => $invoice->ref,__N__                    \'ref_supplier\' => $invoice->ref_supplier,__N__                    \'ref_ext\' => $invoice->ref_ext,__N__                    \'fk_user_author\' => $invoice->user_creation_id,__N__                    \'fk_user_valid\' => $invoice->user_validation_id,__N__                    \'fk_thirdparty\' => $invoice->socid,__N__                    \'type\' => $invoice->type,__N__                    \'status\' => $invoice->status,__N__                    \'total_net\' => $invoice->total_ht,__N__                    \'total_vat\' => $invoice->total_tva,__N__                    \'total\' => $invoice->total_ttc,__N__                    \'date_creation\' => dol_print_date($invoice->datec, \'dayhourrfc\'),__N__                    \'date_modification\' => dol_print_date($invoice->tms, \'dayhourrfc\'),__N__                    \'date_invoice\' => dol_print_date($invoice->date, \'dayhourrfc\'),__N__                    \'date_term\' => dol_print_date($invoice->date_echeance, \'dayhourrfc\'),__N__                    \'label\' => $invoice->label,__N__                    \'paid\' => $invoice->paid,__N__                    \'note_private\' => $invoice->note_private,__N__                    \'note_public\' => $invoice->note_public,__N__                    \'close_code\' => $invoice->close_code,__N__                    \'close_note\' => $invoice->close_note,__N____N__                    \'lines\' => $linesresp__N__            );__N__        }__N__        __N__        foreach($linesinvoice as $invoice) {__N__            $dateinvoice=substr($invoice[\'date_invoice\'], 0, 10);__N__            __N__            // Rule to detect invoice found is for dolistore payment back__N__			$isfordolistore=0;__N__			if ((preg_match(\'/dolistore/i\',$invoice[\'note_private\']) || preg_match(\'/dolistore/i\',$invoice[\'label\']))__N__				&& ! preg_match(\'/agios/i\',$invoice[\'ref_supplier\'])__N__				&& ! preg_match(\'/frais/i\',$invoice[\'ref_supplier\'])__N__				&& ! preg_match(\'/comDolistore/i\',$invoice[\'ref_supplier\'])__N__				) $isfordolistore=1;__N__				__N__			if (! $isfordolistore) {__N__				foreach($invoice[\'lines\'] as $line)__N__				{__N__					if (preg_match(\'/dolistore/i\',$line[\'desc\'])__N__						&& ! preg_match(\'/Remboursement certificat|Remboursement domaine/i\',$line[\'desc\'])__N__						&& ! preg_match(\'/agios/i\',$invoice[\'ref_supplier\'])__N__						&& ! preg_match(\'/frais/i\',$invoice[\'ref_supplier\'])__N__						&& ! preg_match(\'/comDolistore/i\',$invoice[\'ref_supplier\'])__N__						)__N__					{__N__						$isfordolistore++;__N__					}__N__				}__N__			}__N__			__N__			if ($isfordolistore)__N__			{__N__				// Array of already received and paid invoices__N__				$dolistoreinvoices[]=array(__N__					\'dolistore_customer_id\'=>$customer_id,__N__					\'id\'=>$invoice[\'id\'],__N__					\'ref\'=>$invoice[\'ref\'],__N__					\'ref_supplier\'=>$invoice[\'ref_supplier\'],__N__					\'status\'=>$invoice[\'status\'],__N__					\'date\'=>$invoice[\'date_invoice\'],__N__					\'datenohour\'=>$dateinvoice,__N__					\'amount_ht\'=>$invoice[\'total_net\'],__N__					\'amount_vat\'=>$invoice[\'total_vat\'],__N__					\'amount_ttc\'=>$invoice[\'total\'],__N__					\'fk_thirdparty\'=>$invoice[\'fk_thirdparty\']__N__				);__N__			}__N__        }__N__    }__N__    __N__    // Calculte total recived amount__N__    foreach ($dolistoreinvoices as $item) {__N__        $tmpdate=preg_replace(\'/(\\s|T)00:00:00Z/\',\'\',$item[\'date\']);__N__        if (strcmp($tmpdate,\'2013-01-01\') < 0) $alreadyreceived+=$item[\'amount_ttc\'];__N__		else $alreadyreceived+=$item[\'amount_ht\'];__N__    }__N__    /*=========== Payment History ====================*/__N____N__    __N__    __N__    /*$sum_payment_done_before_2013 = \"SELECT SUM(f.total_ttc) as payment_done \";__N__    $sum_payment_done_before_2013 .= \"FROM \".MAIN_DB_PREFIX.\"facture_fourn as f \";__N__    $sum_payment_done_before_2013 .= \"WHERE f.fk_soc = \" . ((int) $customer_id) . \" and f.fk_statut = 2 and f.paye = 1 AND (f.libelle LIKE \'%vente%\' OR f.libelle LIKE \'%dolistore%\') and f.datef < \'2013-01-01\' \";__N__    $resql = $db->query($sum_payment_done_before_2013);__N__    $sum_payment_done_before_2013 = $db->fetch_object($resql);__N__    __N__    $sum_payment_done_after_2013 = \"SELECT SUM(f.total_ht) as payment_done \";__N__    $sum_payment_done_after_2013 .= \"FROM \".MAIN_DB_PREFIX.\"facture_fourn as f \";__N__    $sum_payment_done_after_2013 .= \"WHERE f.fk_soc = \" . ((int) $customer_id) . \" and f.fk_statut = 2 and f.paye = 1 AND (f.libelle LIKE \'%vente%\' OR f.libelle LIKE \'%dolistore%\') and f.datef > \'2013-01-01\' \";__N__    $resql = $db->query($sum_payment_done_after_2013);__N__    $sum_payment_done_after_2013 = $db->fetch_object($resql);__N__    __N__    __