ount, submit, edit or buy modules yet.<br>__N__                [FR] Ce site est toujours en maintenance. Merci de ne pas creer de compte, soumettre, Ã©diter ou acheter des modules.<br>__N__                <br><br>__N__                </div>__N__            <?php endif ?>__N__            <!-- To remove later -->__N____N__            <!-- include welcome message -->__N__            <?php__N__                $__PAGE__TITLE__ = $weblangs->trans(\"indexPageTitle\");__N__                $__PAGE__KEYWORDS__ = $weblangs->trans(\"indexPageKeywords\");__N__                $__PAGE__DESC__ = $weblangs->trans(\"indexPageDescription\");__N__                __N__                if (empty($tag) && empty($cat_id) && empty($cat_extid) && empty($search)) {__N__                    $current_lang = $_SESSION[\'lang\'][\'shortcode\'];__N__                    $welcome_message = $websitepage->fetchAll($website->id, \'\', \'\', 0, 1, array(\'pageurl\' => \'welcome-message\', \'status\' => \'1\', \'lang\' => $website->lang));__N__                    if ($website->lang == $current_lang) {__N__                        includeContainer($welcome_message[key($welcome_message)]->pageurl);__N__                    } else {__N__                        $trans_welcome_message = $websitepage->fetchAll($website->id, \'\', \'\', 0, 1, array(\'fk_page\' => $welcome_message[key($welcome_message)]->id, \'status\' => \'1\', \'lang\' => $current_lang));__N__                        includeContainer($trans_welcome_message[key($trans_welcome_message)]->pageurl);__N__                    }__N__                } else {__N__                    // Define SEO meta tags__N__                    $root_cat_object = new Categorie($db);__N__                    if (!empty($cat_extid)) {__N__                        $result = $root_cat_object->fetch(\'\', \'\', Categorie::TYPE_PRODUCT, $cat_extid);__N__            	    } __N__            	    if (!empty($cat_id)) {__N__            	        $result = $root_cat_object->fetch($cat_i