dminsCat->fetch($idcategoryoftheboardteam);__N__	__N__	$result = $adminsCat->containsObject(\'member\', $membre->id);__N__	if ($result > 0) {__N__	    $isAdmin = true;__N__	    __N__        $customer_id = GETPOSTINT(\'id_customer\');__N__        // Get soc info.__N__        $socInfo = new Societe($db);__N__        $socInfo->fetch($customer_id);__N__        __N__        // Get soc acount email__N__        $sql = \"select rowid from \".MAIN_DB_PREFIX.\"societe_account where fk_soc = \".((int) $customer_id).\" AND fk_website = \".((int) $website->id).\" limit 1\";__N__        $result = $db->query($sql);__N__        $objsql = $db->fetch_object($result);__N__        __N__        // Get account info__N__        $cContacts = $socInfo->contact_array_objects();__N__        foreach ($cContacts as $cContact) {__N__            if ($cContact->ref_ext == $objsql->rowid) {__N__                $customer_firstname = $cContact->firstname;__N__                $customer_lastname = $cContact->lastname;__N__                $customer_email = $cContact->email;__N__                $customer_address = $cContact->address;__N__            }__N__        }__N__	}__N__}__N____N__// Get liste of products__N__$products_ids = array();__N____N__$sql = \'SELECT pf.fk_product, MIN(pf.datec) AS first_date \';__N__$sql .= \'FROM \'.MAIN_DB_PREFIX.\'product_fournisseur_price AS pf, \'.MAIN_DB_PREFIX.\'categorie_product AS cp \';__N__$sql .= \'WHERE pf.fk_soc = \'.((int) $customer_id).\' \';__N__$sql .= \'AND cp.fk_product = pf.fk_product \';__N__$sql .= \'AND cp.fk_categorie = \'.getDolGlobalInt(\"MARKETPLACE_ROOT_CATEGORY_ID\").\' \';__N__$sql .= \'GROUP BY pf.fk_product \';__N__$sql .= \'ORDER BY first_date ASC\';__N____N__if ($result_products = $db->query($sql)) {__N__	while ($product_id = $result_products->fetch_object()) {__N__        $products_ids[] = $product_id->fk_product;__N__	}__N__}__N____N__// END PHP __N__?>__N__<div id=\"page\">__N__  <div class=\"header-container\">__N__    <?php __N__    