INSERT INTO llx_website_page(rowid, fk_page, fk_website, pageurl, aliasalt, title, description, lang, image, keywords, status, date_creation, tms, import_key, grabbed_from, type_container, htmlheader, content, author_alias, allowed_in_frames) VALUES(34__+MAX_llx_website_page__, null, __WEBSITE_ID__, 'order-details', '', 'order details', '', '', '', '', '1', '2024-04-24 10:40:25', '2025-01-15 11:06:39', null, '', 'page', '', '<?php // BEGIN PHP__N__session_start(); // start session__N__require_once DOL_DOCUMENT_ROOT . \'/categories/class/categorie.class.php\';__N__require_once DOL_DOCUMENT_ROOT . \'/core/lib/categories.lib.php\';__N__include_once DOL_DOCUMENT_ROOT.\'/societe/class/societeaccount.class.php\';__N__require_once DOL_DOCUMENT_ROOT.\"/commande/class/commande.class.php\";__N__include_once DOL_DOCUMENT_ROOT.\'/ecm/class/ecmfiles.class.php\';__N__require_once DOL_DOCUMENT_ROOT.\'/core/lib/security2.lib.php\';__N__include_once DOL_DOCUMENT_ROOT.\'/core/lib/date.lib.php\';__N____N____N__$tag = GETPOST(\'tag\', \'alpha\');__N__$cat_id = GETPOST(\'cat\', \'alpha\');__N__$search = GETPOST(\'search_query\', \'alpha\');__N__$order_ref = GETPOST(\'ref\', \'alpha\');__N____N__// If not login redirect to login.__N__if(empty($_SESSION[\'user\'][\'id_user\'])){__N__    redirectToContainer(\'login-register\');__N__}__N____N__// Check if all necessary parameters are passed.__N__if (empty($order_ref)) {__N__	redirectToContainer(\'404\');__N__    exit;__N__}__N____N__// Get Account ID.__N__$user_id = $_SESSION[\'user\'][\'id_user\'];__N__$customer_account = new SocieteAccount($db);__N__$result = $customer_account->fetch($user_id);__N__$customer_id = $customer_account->fk_soc;__N____N__// Get customer ID.__N__$customer = new Societe($db);__N__$result = $customer->fetch($customer_id);__N____N__// To render view of another user__N__if (!empty(GETPOST(\'id_customer\'))) {__N__    // Only for members with a specific role__N__    require_once DOL_DOCUMENT_ROOT.\"/adherents/class/adherent.class.php\";__N__    $membre = new Adherent($db);__N__    $result = $membre->fetch(\'\', 0, $customer_id);__N__    __N__    $adminsCat = new Categorie($db);__N__	$adminsCat->fetch(1);__N__	__N__	$result = $adminsCat->containsObject(\'member\', $membre->id);__N__	if ($result > 0) {__N__	    __N__        $customer_id = GETPOST(\'id_customer\');__N__	}__N__}__N____N__// Check if there is a paid order for this customer with the sent information.__N__$sql_find_order = \"SELECT c.rowid, c.ref, c.fk_statut, c.fk_soc, c.date_creation, c.module_source \";__N__$sql_find_order .= \"FROM \".MAIN_DB_PREFIX.\"commande as c \";__N__$sql_find_order .= \"WHERE c.fk_soc = \" . $customer_id . \" and c.ref = \'\" . $order_ref . \"\' and c.fk_statut= 1 and (c.facture = 1 || c.ref_ext IS NOT NULL) and c.module_source = \'Marketplace\' \";__N__$sql_find_order .= \"limit 1\";__N__$find_order = $db->query($sql_find_order);__N__$obj_order = $db->fetch_object($find_order);__N____N__if (empty($obj_order)) {__N__    redirectToContainer(\'404\');__N__} else {__N__    // Get order object__N__    $order_ref = $obj_order->ref;__N__    $order_object = new Commande($db);__N__    $result = $order_object->fetch(\'\', $order_ref);__N__    if ($result < 0) {__N__    	redirectToContainer(\'404\');__N__    }__N__    __N__    // Get invoice object__N__    $order_object->fetchObjectLinked();__N__    __N__    if (!empty($order_object->linkedObjects[\'facture\'])) {__N__        $linked_invoices = $order_object->linkedObjects[\'facture\'];__N__        $last_invoice_object = $linked_invoices[reset(array_keys($linked_invoices))];__N__        if ($last_invoice_object instanceof Facture) {__N__            $invoice_object = $last_invoice_object;__N__        }__N__        __N__        // Get payment details__N__        $sql_payment_details = \"SELECT c.libelle AS payment_method \";__N__        $sql_payment_details .= \"FROM \";__N__        $sql_payment_details .= \"\".MAIN_DB_PREFIX.\"paiement_facture AS pf \";__N__        $sql_payment_details .= \"JOIN \".MAIN_DB_PREFIX.\"paiement AS p ON pf.fk_paiement = p.rowid \";__N__        $sql_payment_details .= \"LEFT JOIN \".MAIN_DB_PREFIX.\"c_paiement AS c ON p.fk_paiement = c.id \";__N__        $sql_payment_details .= \"WHERE \";__N__        $sql_payment_details .= \"pf.fk_facture = \" . $invoice_object->id ;__N__        __N__        $find_payment_details = $db->query($sql_payment_details);__N__        $obj_payment = $db->fetch_object($find_payment_details);__N__        $payment_method = $obj_payment->payment_method;__N__        __N__        __N__        // Generate invoice PDF (share it)__N__        $sql_invoice_pdf = \"select lef.rowid as invoice_pdf_id \";__N__        $sql_invoice_pdf .= \"from \".MAIN_DB_PREFIX.\"ecm_files lef \";__N__        $sql_invoice_pdf .= \"where lef.src_object_type = \'facture\' and lef.src_object_id = \" . $invoice_object->id ;__N____N__        $find_invoice_pdf = $db->query($sql_invoice_pdf);__N__        $obj_invoice_pdf = $db->fetch_object($find_invoice_pdf);__N__        $invoice_pdf_id = $obj_invoice_pdf->invoice_pdf_id;__N____N__        $ecmfile = new EcmFiles($db);__N__    	$result = $ecmfile->fetch($invoice_pdf_id);__N__    	if ($result > 0 && empty($ecmfile->share)) {__N__    		$ecmfile->share = getRandomPassword(true);__N__    		$result = $ecmfile->update($user);__N__    		if ($result < 0) {__N__    			setEventMessages($ecmfile->error, $ecmfile->errors, \'warnings\');__N__    		}__N__    	}__N__    	$invoice_pdf_link = DOL_URL_ROOT . \"/document.php?hashp=\" . $ecmfile->share;__N____N__    } else {__N__        $payment_method = \"\";__N__        $invoice_object = null;__N__    }__N__}__N____N____N__// END PHP __N__?>__N__<div id=\"page\">__N__  <div class=\"header-container\">__N__    <?php__N__        includeContainer(\'header\'); __N__        $__PAGE__TITLE__ = $weblangs->trans(\"orderDetailsPageTitle\");__N__    ?>__N__  </div>__N____N__  <div class=\"columns-container\">__N__    <div id=\"columns\" class=\"container\">__N__        <!--  Breadcrumb  -->__N__        <div class=\"breadcrumb clearfix\">__N__        	<a class=\"home\" href=\"index.php\" title=\"<?php print $weblangs->trans(\"backHome\"); ?>\"><i class=\"icon-home\"></i></a>__N__        	<span class=\"navigation-pipe\">&gt;</span> <a href=\"order-history.php\" title=\"<?php print $weblangs->trans(\"orderHistory\"); ?>\" data-gg=\"\"><?php print $weblangs->trans(\"orderHistory\"); ?></a>__N__        	<span class=\"navigation-pipe\">&gt;</span> <?php print $weblangs->trans(\"orderDetails\"); ?>__N__        </div>__N__        <!--  Breadcrumb  -->__N__      <div id=\"slider_row\" class=\"row\">__N__        <div id=\"top_column\" class=\"center_column col-xs-12 col-sm-12\"></div>__N__      </div>__N__      <div class=\"row\">__N__        <!-- include lefet sidebar container -->__N__        <?php includeContainer(\'sidebar\'); ?>__N__        <div id=\"center_column\" class=\"center_column col-xs-12 col-sm-9\">__N__          <div class=\"clearfix\">__N__                <h1 class=\"page-heading\"><?php print $weblangs->trans(\"orderDetails\"); ?></h1>__N__                <div id=\"block-history\" class=\"block-center\">__N__                    <div id=\"block-order-detail\" class=\"unvisible\" style=\"display: block;\">__N__                    	<div class=\"info-order box\">__N__                    	    <p class=\"dark\">__N__                				<?php print $weblangs->trans(\"orderReference\"); ?> <strong><?php print $order_object->ref; ?></strong> - <?php print $weblangs->trans(\"placedOn\"); ?> <strong><?php print dol_print_date($order_object->date, \'day\'); ?></strong>__N__                				<span style=\"background-color:#DDEEFF; border-color:#DDEEFF;padding: 4px 10px;float:right;\" class=\"label dark\"><?php print $weblangs->trans(\"paymentAccepted\"); ?></span>__N__                			</p>__N__                    		<p><strong class=\"dark\"><?php print $weblangs->trans(\"paymentMethod\"); ?> : </strong> <span class=\"color-myaccount\"><?php print $payment_method; ?></span></p>__N__                    		<p class=\"download_invoice\">__N__                    			<i class=\"icon-file-text\"></i>__N__                    			<?php__N__                    			    if ($invoice_object === null) {__N__                    			        print $weblangs->trans(\"ifYouNeedAnInvoice\", \"https://wwwold.dolistore.com\", \"wwwold.dolistore.com\");__N__                    			    } else{ ?>__N__                    			        <a target=\"_blank\" href=\"<?php print $invoice_pdf_link; ?>\"><?php print $weblangs->trans(\"downloadInvoicePDF\"); ?></a>__N__                    			    <?php } __N__                    			?>__N__                    		</p>__N__                    	</div>__N__                    __N__                    __N__                    	<div class=\"adresses_bloc\">__N__                    		<div class=\"row\">__N__                    			<div class=\"col-xs-12 col-sm-6\">__N__                    				<ul class=\"address item full_width box\">__N__                    					<li>__N__                    						<h3 class=\"page-subheading\"><?php print $weblangs->trans(\"invoiceAddress\"); ?></h3>__N__                    					</li>__N__                    					<li><span class=\"address_firstname\"><?php print $customer->name; ?></span></li>__N__                    					<li><span class=\"address_address1\"><?php print $customer->address; ?></span></li>__N__                    					<li><span class=\"address_Country:name\"><?php print $customer->country; ?></span></li>__N__                    					<li><span class=\"address_phone\"></span></li>__N__                    				</ul>__N__                    			</div>__N__                    		</div>__N__                    	</div>__N__                    __N__                    	<form action=\"\" method=\"post\">__N__                    		<div id=\"order-detail-content\" class=\"table_block table-responsive\">__N__    