 of another user__N__    if (!empty(GETPOST(\'id_customer\'))) {__N__        // Only for members with a specific role__N__        require_once DOL_DOCUMENT_ROOT.\"/adherents/class/adherent.class.php\";__N__        $membre = new Adherent($db);__N__        $result = $membre->fetch(\'\', 0, $customer_id);__N__        __N__        $adminsCat = new Categorie($db);__N__    	$adminsCat->fetch(1);__N__    	__N__    	$result = $adminsCat->containsObject(\'member\', $membre->id);__N__    	if ($result <= 0) {__N__            redirectToContainer(\'manage-products\');__N__    	}__N__    } else {__N__        redirectToContainer(\'manage-products\');__N__    }__N__}__N__// END PHP __N__?>__N__<div id=\"page\">__N__  <div class=\"header-container\">__N__    <?php __N__        includeContainer(\'header\');__N__        $__PAGE__TITLE__ = $weblangs->trans(\"productStatisticsPageTitle\");__N__    ?>__N__  </div>__N____N__  <div class=\"columns-container\">__N__    <div id=\"columns\" class=\"container\">__N__        <!--  Breadcrumb  -->__N__        <div class=\"breadcrumb clearfix\">__N__        	<a class=\"home\" href=\"index.php\" title=\"Return to Home\"><i class=\"icon-home\"></i></a>__N__        	<span class=\"navigation-pipe\">&gt;</span> <a href=\"myaccount.php\" title=\"<?php print $weblangs->trans(\"myAccount\"); ?>\" data-gg=\"\"><?php print $weblangs->trans(\"manageProducts\"); ?></a>__N__        	<span class=\"navigation-pipe\">&gt;</span> <?php print $weblangs->trans(\"orderHistory\"); ?>__N__        </div>__N__        <!--  Breadcrumb  -->__N__      <div id=\"slider_row\" class=\"row\">__N__        <div id=\"top_column\" class=\"center_column col-xs-12 col-sm-12\"></div>__N__      </div>__N__      <div class=\"row\">__N__        <!-- include lefet sidebar container -->__N__        <?php includeContainer(\'sidebar\'); ?>__N__        <div id=\"center_column\" class=\"center_column col-xs-12 col-sm-9\">__N__          <div class=\"clearfix\">__N__                <?php__N__                    // Get product object__N__                    $product = new Product($db);__N__                    $result = $product->fetch($product_id);__N__                ?>__N__                <h1 class=\"page-heading\"><?php print $weblangs->trans(\"product\"); ?> : <?php print $product->label; ?></h1>__N__                <div id=\"block-history\" class=\"block-center\">__N__                    <div id=\"block-order-detail\" class=\"unvisible\" style=\"display: block;\">__N__                		<div id=\"order-detail-content\" class=\"table_block table-responsive\">__N__                			<table class=\"table table-bordered table-bordered-block-order-detail order-history\">__N__                				<thead>__N__                					<tr>__N__                						<th class=\"item\" width=\"50\">#</th>__N__                						<th class=\"item\" width=\"150\"><?php print $weblangs->trans(\"orderRef\"); ?></th>__N__                						<th class=\"item\"><?php print $weblangs->trans(\"ThirdPartyName\"); ?></th>__N__                						<th class=\"item\" width=\"120\"><?php print $weblangs->trans(\"date\"); ?></th>__N__                						<th class=\"last_item\"><?php print $weblangs->trans(\"amountEarned\"); ?></th>__N__                					</tr>__N__                				</thead>__N__                				<tbody>__N__                				    <?php__N__                				    // Get list of orders for this product __N__                					$orders_for_product = \"SELECT c.rowid, c.ref, c.fk_statut, c.fk_soc, c.date_commande, c.module_source , d.total_ht \";__N__                                    $orders_for_product .= \"FROM \".MAIN_DB_PREFIX.\"commande as c, \".MAIN_DB_PREFIX.\"commandedet as d \";__N__                                    $orders_for_product .= \"WHERE c.rowid = d.fk_commande and d.fk_product = \" . ((int) $product->id) . \" and c.fk_statut IN (1,3) and (c.facture = 1 || c.ref_ext IS NOT NULL) and c.module_source = \'Marketplace\' \";__N__                                    $orders_for_product .= $db->order(\"date_commande\", \"DESC\");__N__                                    if ($result_orders = $db->query($orders_for_product)) {__N__                                        $i = 1;__N__                                    	while ($order_details = $result_orders->fetch_object()) {__N__                                    	    if (!empty($conf->cache[\'thirdparty\'][$order_details->fk_soc])) {__N__                                    	        $customer = $conf->cache[\'thirdparty\'][$order_details->fk_soc];__N__                                    	    } else {__N__                                    	        $customer = new Societe($db);__N__                                    	        $customer->fetch($order_details->fk_soc);__N__                                    	    __N__                                    	        $conf->cache[\'thirdparty\'][$order_details->fk_soc] = $customer;__N__                                    	    }__N__                                    	    __N__                                    	    // Calculate discount for this order line__N__                                            $TOTAL_DISCOUNTS = 0;__N__                                            $sum_all_discounts = \"SELECT SUM(d.total_ht) as total \";__N__                                            $sum_all_discounts .= \"FROM \".MAIN_DB_PREFIX.\"commande as c, \".MAIN_DB_PREFIX.\"commandedet as d \";__N__                                            $sum_all_discounts .= \"WHERE c.rowid = d.fk_commande \";__N__                                            $sum_all_discounts .= \"AND c.rowid = \".$order_details->rowid.\" \";__N__                                            $sum_all_discounts .= \"AND d.description LIKE \'\" . ((int) $product->id) . \"# %\' \";__N__                                            $sum_all_discounts .= \"AND (c.facture = 1 OR c.note_private LIKE \'%Order imported%\' OR c.ref_ext IS NOT NULL) \";__N__                                            $sum_all_discounts .= \"AND c.module_source = \'Marketplace\' \";__N__                                            $resql = $db->query($sum_all_discounts);__N__                                            $sells_discounts = $db->fetch_object($resql);__N__                                            $TOTAL_DISCOUNTS = $sells_discounts->total;__N__                                    ?>__N__                    					<tr class=\"item\">__N__                    					    <td>__N__                    					        <p><?php print $i; ?></p>__N__                    					    </td>__N__                    					    <td>__N__                    					        <p><?php print $order_details->ref; ?></p>__N__                    					    </td>__N__                    					    <td>__N__                    						        <b><?php print $customer->name; ?></b><br>__N____N__                    						        <small class=\"cart_ref\">__N__                                                        <?php print $weblangs->trans(\"Email\"); ?>: <?php print $customer->email; ?><br>__N__                                                        <?php print $weblangs->trans(\"registredOn\"); ?>: <?php print dol_print_date($customer->date_creation, \'day\'); ?>__N__                    						        </small>__N____N__                    						</td>__N__                    						<td>__N__                    						    <p><?php print dol_print_date($order_details->date_commande, \'day\'); ?></p>__N__											</td>__N__                    						<td width=\"160\">__N__                    							<p><span class=\"price\"><?php print price($order_details->total_ht); ?> â¬</span> <?php if ($TOTAL_DISCOUNTS) { print \"(\" . price($TOTAL_DISCOUNTS) . \")\"; } ?> </p>__N__                    						</td>__N__                    					</tr>__N__                					    <?php__N__                					    $i++;__N__                                    	}__N__                                    }__N__                                    ?>__N__                				</tbody>__N__                			</table>__N__                		</div>__N__                    </div>__N__                </div>__N__                <ul class=\"footer_links clearfix\">__N__                	<li>__N__                		<a class=\"btn btn-default button button-small\" href=\"myaccount.php\">__N__                			<span>__N__                				<i class=\"icon-chevron-left\"></i> <?php print $weblangs->trans(\"backToYourAccount\"); ?>__N__                			</span>__N__                		</a>__N__                	</li>__N__                	<li>__N__                		<a class=\"btn btn-default button button-small\" href=\"index.php\">__N__                			<span><i class=\"icon-chevron-left\"></i> <?php print $weblangs->trans(\"Home\"); ?></span>__N__                		</a>__N__                	</li>__N__                </ul>__N__          </div>__N__        </div>__N__      </div>__N__    </div>__N__  </div>__N__  <!-- .columns-container -->__N__  <div class=\"footer-container\">__N__    <?php includeContainer(\'footer\'); ?>__N__  </div>__N__</div>', '', 0);

-- Page ID 42__+MAX_llx_website_page__ - Aliases products-list-grid --;
INSERT INTO llx_website_page(rowid, fk_page, fk_website, pageurl, aliasalt, title, description, lang, image, keywords, status, date_creation, tms, import_key, grabbed_from, type_container, htmlheader, content, author_alias, allowed_in_frames) VALUES(42__+MAX_llx_website_page__, null, __WEBSITE_ID__, 'products-list-grid', '', 'product list grid', 'product list grid', '', '', '', '1', '2024-02-02 16:48:47', '2025-01-16 14:52:28', null, '', 'blogpost', '', '<script type=\"text/javascript\">__N__  var displayList = false;__N__  var page_name = \'tt\';__N__  var quickView = false;__N__  var request = window.location.href;__N__  var display = \"grid\";__N__</script>__N____N__<?php // BEGIN PHP__N__includeContainer(\'_library_page_lib.php\', 1);__N__$current_lang = $_SESSION[\'lang\'][\'code\'];__N__$current_lang_short_code = $_SESSION[\'lang\'][\'shortcode\'];__N____N____N__$root_cat_object = new Categorie($db);__N____N__if(GETPOST(\'cat\', \'alpha\')){__N__	$root_category_id = GETPOST(\'cat\', \'alpha\');__N__}__N____N__if(GETPOST(\'extcat\', \'alpha\')){__N__	$root_category_extid = GETPOST(\'extcat\', \'alpha\');__N__}__N____N__$tag = GETPOST(\'tag\', \'alpha\');__N__$list = GETPOST(\'list\', \'alpha\');__N__$search_words = GETPOST(\'search_query\', \'alpha\');__N__$cat = GETPOST(\'cat\', \'alpha\');__N__$extcat = GETPOST(\'extcat\', \'alpha\');__N____N__$isHomePage = !GETPOST(\'tag\', \'alpha\') && !GETPOST(\'cat\', \'alpha\') && !GETPOST(\'list\', \'alpha\') && !GETPOST(\'search_query\', \'alpha\') && !GETPOST(\'extcat\', \'alpha\');__N____N__if (!empty($search_words)) {__N__    $keywords = explode(\" \", $search_words);__N__    $request = \'\';__N__    $order = \'ORDER BY CASE \';__N____N__    foreach ($keywords as $key => $value) {__N__        if ($key != 0) {__N__            $request .= \" OR \";__N__        }__N__        $value = $db->escape($db->escapeforlike($value));__N____N__        // Build the search conditions for labels, notes, and vendor names__N__        $request .= \"(ol.label LIKE \'%\" . $value . \"%\' OR __N__                    ol.note LIKE \'\" . $value . \" %\'  OR __N__                    ol.note LIKE \'% \" . $value . \" %\'  OR __N__                    ol.note LIKE \'% \" . $value . \"\' OR__N__                    ol.note LIKE \'%>\" . $value . \" %\' OR__N__                    ol.note LIKE \'% \" . $value . \"<%\' OR__N__                    s.nom LIKE \'%\" . $value . \"%\')\";__N____N__        // Define the order of results based on matching criteria__N__        // Level 1: Full match in the label__N__        $order .= \"WHEN ol.label LIKE \'\" . $value . \" %\' THEN 1 \";__N__        $order .= \"WHEN ol.label LIKE \'% \" . $value . \" %\' THEN 1 \";__N__        $order .= \"WHEN ol.label LIKE \'% \" . $value . \"\' THEN 1 \";__N____N__        // Level 2: Partial match in the label or vendor name__N__        $order .= \"WHEN ol.label LIKE \'%\" . $value . \"%\' THEN 2 \";__N__        $order .= \"WHEN s.nom LIKE \'%\" . $value . \"%\' THEN 2 \";__N____N__        // Level 3: Full Match in the notes(Long description) field__N__        $order .= \"WHEN ol.note LIKE \'\" . $value . \" %\' THEN 3 \";__N__        $order .= \"WHEN ol.note LIKE \'% \" . $value . \" %\' THEN 3 \";__N__        $order .= \"WHEN ol.note LIKE \'% \" . $value . \"\' THEN 3 \";__N__        $order .= \"WHEN ol.note LIKE \'%>\" . $value . \" %\' THEN 3 \";__N__        $order .= \"WHEN ol.note LIKE \'% \" . $value . \"<%\' THEN 3 \";__N____N__        //$order .= \"WHEN ol.description LIKE \'%\" . $value . \"%\' THEN 4 \";__N__    }__N__    __N__    // Finalize filter and order__N__    $filter = \"($request) AND (o.tosell = 1) \";__N__    $order .= \" END, o.datec DESC\";  // Complete the order clause__N__} else {__N__    $filter = \'o.tosell = 1\';__N__}__N____N__$page_no = GETPOST(\'p\', \'alpha\');__N__if(empty($page_no)) {__N__	$page_no = 1;__N__}__N____N__$limit = GETPOST(\'n\', \'alpha\');__N__if(empty($limit)) {__N__	$limit = 11;__N__}else{__N__	$limit = $limit -1;__N__}__N____N__$orderby = GETPOST(\'orderby\', \'alpha\');__N__switch ($orderby) {__N__	case \'price\':__N__		$sortfield = \'price_ttc\';__N__		break;__N__	case \'name\':__N__		$sortfield = \'label\';__N__		break;__N__	case \'reference\':__N__		$sortfield = \'ref\';__N__		break;__N__	default:__N__		$sortfield = \'datec\';__N__		break;__N__}__N____N__$orderway = GETPOST(\'orderway\', \'alpha\');__N__switch ($orderway) {__N__	case \'asc\':__N__		$sortorder = \"ASC\";__N__		break;__N__	case \'desc\':__N__		$sortorder = \"DESC\";__N__		break;__N__	default:__N__		$sortorder = \"DESC\";__N__		break;__N__}__N____N__if ($page_no == 1) {__N__	$offset = 0;__N__}else{__N__	$offset = ( $limit * ($page_no-1) )+($page_no - 1);__N__}__N____N__$id = null;__N__$type  = Categorie::TYPE_PRODUCT;__N__$is_for_sell = 1;__N____N__if (empty($tag)) {__N__	if (isset($root_category_id) || isset($root_category_extid)) {__N__	    if (isset($root_category_id)) {__N__	        $result = $root_cat_object->fetch($root_category_id,\"\");__N__	    } else {__N__	        $result = $root_cat_object->fetch(\'\', \'\', Categorie::TYPE_PRODUCT, $root_category_extid);__N__	    }__N__		__N__		if ($result <= 0) {__N__			redirectToContainer(\'404\');__N__		}__N__		$root_category_id = $root_cat_object->id;__N__		$root_category_label = $root_cat_object->label;__N__	} else {__N__	    if ($isHomePage && getDolGlobalInt(\'MARKETPLACE_ROOT_CATEGORY_ID_BIS\')) {__N__    	    // To force ID of category for v20 products only on Home page__N__    		$result = $root_cat_object->fetch(getDolGlobalInt(\"MARKETPLACE_ROOT_CATEGORY_ID_BIS\"));__N__	    } else {__N__    		$result = $root_cat_object->fetch(getDolGlobalInt(\"MARKETPLACE_ROOT_CATEGORY_ID\"));__N__	    }__N__		if ($result <= 0) {__N__			redirectToContainer(\'404\');__N__		}__N__		$root_category_label = $root_cat_object->label;__N__		$root_category_id = $root_cat_object->id;__N__	}__N__} else {__N__    if ($tag == \"Specials\") {__N__        $result = $root_cat_object->fetch(getDolGlobalInt(\"MARKETPLACE_SPECIAL_CATEGORY_ID\"));__N__    } else {__N__        $result = $root_cat_object->fetch(\"\", $tag);__N__    }__N__	__N__	if ($result <= 0) {__N__		dol_print_error($db, $root_cat_object->error); exit;__N__	}__N__	$root_category_label = $root_cat_object->label;__N__	$root_category_id = $root_cat_object->id;__N__}__N__$root_category_type = $root_cat_object->type;__N____N__// If Goodies category, redirect to specific page__N__if ($root_category_label == \'Goodies\') {__N__    redirectToContainer(\'goodies\');__N__}__N____N____N__if($root_category_id == getDolGlobalInt(\"MARKETPLACE_ROOT_CATEGORY_ID\") && empty($tag) && empty($search_words) && $list == \"new\") {__N__    $delay_for_new = (int) getDolGlobalInt(\'MARKETPLACE_DELAY_FOR_NEW\', 30);__N__    $dateTime = new \\DateTime();__N__    $interval = new \\DateInterval(\'P\'.$delay_for_new.\'D\');__N__    $dateTime->sub($interval);__N__    $request = \"o.datec > \'\" . $dateTime->format(\'Y-m-d H:i:s\') . \"\'\";__N__    $filter = \"(\".$request . \") And (o.tosell=1)\";__N__}__N____N__// END PHP ?>__N____N__<?php__N__// Count SQL__N__/*$countSql = \"SELECT c.fk_product \";__N__$countSql .= \"FROM llx_categorie_product as c, \";__N__$countSql .= \"llx_product as o, \";__N__$countSql .= \"llx_product_lang as ol \";__N__$countSql .= \"WHERE o.entity IN (1) AND c.fk_categorie = \".$root_category_id.\" AND c.fk_product = o.rowid AND ol.fk_product = o.rowid \";__N__$countSql .= \"AND ol.lang = \'\".$db->escape($current_lang).\"\' AND \";__N__$countSql .= $filter;__N____N__$resql = $db->query($countSql);__N__$count_prods = $resql->num_rows;__N____N__// PRODUCT SQL__N__$sql = \"SELECT c.fk_product as id, o.ref, o.ref_ext, ol.label, ol.description, o.datec, o.price_ttc \";__N__$sql .= \"FROM llx_categorie_product as c, \";__N__$sql .= \"llx_product as o, \";__N__$sql .= \"llx_product_lang as ol \";__N__$sql .= \"WHERE o.entity IN (1) AND c.fk_categorie = \".$root_category_id.\" AND c.fk_product = o.rowid AND ol.fk_product = o.rowid \";__N__$sql .= \"AND ol.lang = \'\".$db->escape($current_lang).\"\' AND \";__N__$sql .= $filter;*/__N____N__// Count SQL__N__$countSql = \"SELECT c.fk_product \";__N__$countSql .= \"FROM llx_categorie_product as c \";__N__$countSql .= \"INNER JOIN llx_product as o ON c.fk_product = o.rowid \";__N__$countSql .= \"INNER JOIN llx_product_lang as ol ON ol.fk_product = o.rowid \";__N__$countSql .= \"LEFT JOIN llx_product_fournisseur_price as pfp ON pfp.fk_product = o.rowid \";__N__$countSql .= \"LEFT JOIN llx_societe as s ON pfp.fk_soc = s.rowid \";__N__$countSql .= \"WHERE o.entity IN (1) AND c.fk_categorie = \" . ((int) $root_category_id) . \" AND \";__N__$countSql .= \"ol.lang = \'\" . $db->escape($current_lang) . \"\' AND \";__N__$countSql .= $filter;__N__$sql .= \" GROUP BY c.fk_product \"; // Added GROUP BY clause to handle multiple supplier prices__N____N__$resql = $db->query($countSql);__N__$count_prods = $resql->num_rows;__N____N__// PRODUCT SQL__N__$sql = \"SELECT c.fk_product as id, o.ref, o.ref_ext, ol.label, ol.description, o.datec, o.price_ttc \";__N__$sql .= \"FROM llx_categorie_product as c \";__N__$sql .= \"INNER JOIN llx_product as o ON c.fk_product = o.rowid \";__N__$sql .= \"INNER JOIN llx_product_lang as ol ON ol.fk_product = o.rowid \";__N__$sql .= \"LEFT JOIN llx_product_fournisseur_price as pfp ON pfp.fk_product = o.rowid \";__N__$sql .= \"LEFT JOIN llx_societe as s ON pfp.fk_soc = s.rowid \";__N__$sql .= \"WHERE o.entity IN (1) AND c.fk_categorie = \" . ((int) $root_category_id) . \" AND \";__N__$sql .= \"ol.lang = \'\" . $db->escape($current_lang) . \"\' AND \";__N__$sql .= $filter;__N__$sql .= \" GROUP BY c.fk_product, o.ref, o.ref_ext, ol.label, ol.description, o.datec, o.price_ttc, s.nom\"; // Added GROUP BY clause to handle multiple supplier prices__N____N__if ($sortfield == \'datec\' && $sortorder == \'DESC\' && !empty($search_words)) {__N__    $sql .= \" \" . $order;__N__} else {__N__    // Home page: random order__N__    if ($isHomePage) {__N__        $sql .= \" ORDER BY RAND() \";__N__    } else {__N__        $sql .= $db->order($sortfield, $sortorder);__N__    }__N__}__N____N__if ($limit > 0 || $offset > 0) {__N__	$sql .= $db->plimit($limit + 1, $offset);__N__}__N____N__$total_no_of_pages = ceil($count_prods / ($limit + 1));__N__$second_last = $total_no_of_pages - 1; // total pages minus 1__N__$previous_page = $page_no - 1;__N__$next_page = $page_no + 1;__N__$test = \"\";__N__?>__N__<?php if ($count_prods > 0 && (strlen($search_words) == 0 || strlen($search_words) > 1)): ?>__N__	<?php if (GETPOST(\'cat\', \'alpha\') != \'\' || GETPOST(\'extcat\', \'alpha\') != \'\'): ?>__N__	    <section id=\"category_info_section\" contenteditable=\"true\">__N__    		<div class=\"content_scene_cat\">__N__    		    <?php__N__        		    $__PAGE__TITLE__ = $root_cat_object->label;__N__                    $__PAGE__DESC__ = $root_cat_object->description;__N__    		    ?>__N__    			<!-- Category image -->__N__    			<div class=\"content_scene_cat_bg\">__N__    				<div class=\"cat_desc\">__N__    					<?php__N__    					    if ($root_cat_object->id == getDolGlobalInt(\"MARKETPLACE_ROOT_CATEGORY_ID\")) {__N__                                print \'<span class=\"category-name\">\' . $weblangs->trans(\"newProducts\") . \'</span>\';__N__                            } else {__N__                                print \'<span class=\"category-name\">\' . $root_cat_object->label . \'</span>\';__N__                            }__N__                        ?>__N__    					<div class=\"rte\">__N__    						<p>__N__    						<?php __N__    						    if ($root_cat_object->id != getDolGlobalInt(\"MARKETPLACE_ROOT_CATEGORY_ID\")) {__N__    						        print $root_cat_object->description;__N__    						    }__N__    						?>__N__						    </p>__N__    					</div>__N__    				</div>__N__    			</div>__N__    		</div>__N__		</section>__N__	<?php endif ?>_