__N__if (!defined(\'USEDOLIBARRSERVER\')) {__N__	$order_page_url = \"order.php\";__N__} else {__N__	$order_page_url = \"index.php?website=\".$website->ref.\"&pageref=order\";__N__}__N____N__$product_id = GETPOST(\'product_id\', \'int\');__N__$product_thumb = GETPOST(\'product_thumb\');__N__$product_qty = GETPOST(\'product_quantity\', \'int\');__N____N__if ($product_id && $product_qty) {__N____N__	$object = new Product($db);__N__	$get_product = $object->fetch($product_id);__N__	__N__	// check discount and calculate discounted price__N__    $discount = marketplace_check_discount($object->id);__N__    $discounted_price = marketplace_calculate_discounted_price($object->price_ttc, $discount);__N__    __N__    if ($discounted_price != $object->price_ttc) {__N__        $price_to_display = $discounted_price;__N__    } else {__N__        $price_to_display = $object->price_ttc;__N__    }__N____N__    $new_product[\'product_id\'] = $object->id;__N__    $new_product[\'product_thumb\'] = $product_thumb;__N__    $new_product[\'product_name\'] = $object->label;__N__    $new_product[\'product_ref\'] = $object->ref;__N__    $new_product[\'product_price\'] = $price_to_display;__N__    $new_product[\'product_qty\'] = $product_qty;__N____N__        if(isset($_SESSION[\'products\'])){  //if session var already exist__N____N__            if(isset($_SESSION[\'products\'][$new_product[\'product_id\']])) //check item exist in products array__N__            {__N__                unset($_SESSION[\'products\'][$new_product[\'product_id\']]); //unset old item__N__            }__N__        }__N____N__        $_SESSION[\'products\'][$new_product[\'product_id\']] = $new_product;	//update products with new item array__N____N__    $total_items = count($_SESSION[\'products\']); //count total items__N____N__    $popup_product_html = \'__N__        <span class=\"cross\" title=\"Close window\"></span>__N__        <h2>__N__            <i class=\"icon-ok\"></i>\'.$weblangs->trans(\"ProductAddedSuccessfully\").\'__N__        </h2>__N__        <div class=\"product-image-container layer_cart_img\">__N__            <img class=\"layer_cart_img img-responsive\" src=\"\'.$product_thumb.\'\" alt=\"\'.$object->label.\'\" title=\"\'.$object->label.\'\">__N__        </div>__N__        <div id=\"layer_cart_product_info\">__N__            <span id=\"layer_cart_product_title\" class=\"product-name\">\'.$object->label.\'</span>__N__            <span id=\"layer_cart_product_attributes\"></span>__N__            <div>__N__            <strong class=\"dark\">\'.$weblangs->trans(\"Quantity\").\' : </strong>__N__            <span id=\"layer_cart_product_quantity\">\'.$product_qty.\'</span>__N__            </div>__N__            <div>__N__            <strong class=\"dark\">\'.$weblangs->trans(\"total\").\' : </strong>__N__            <span id=\"layer_cart_product_price\">(\'.($product_qty * $price_to_display).\' â¬)</span>__N__            </div>__N__        </div>\';__N____N__    die(json_encode(array(\'items\'=>$total_items,\'popup_product_html\'=>$popup_product_html))); //output json__N____N__}__N____N__if (GETPOST(\'load_cart\') && GETPOST(\'load_cart\') == 1) {__N__    if (isset($_SESSION[\'products\']) && count($_SESSION[\'products\']) > 0) { //if we have session variable__N__    __N__    __N__        $cart_box = \'__N__        <p class=\"cart-buttons\">__N__            <a id=\"button_order_cart\" class=\"btn btn-default button button-small\" href=\"\' . $order_page_url . \'\" title=\"Check out\" rel=\"nofollow\">__N__                <span> \'.$weblangs->trans(\"cartCheckout\").\' <i class=\"icon-chevron-right right\"></i>__N__                </span>__N__            </a>__N__        </p>__N__        \';__N__        __N__        $total = 0;__N__        foreach($_SESSION[\'products\'] as $cart_product){ //loop though items and prepare html content__N____N__            $cart_box .= \"<dl class=\\\"products\\\">__N__                <dt class=\\\"item\\\" data-id=\\\"cart_block_product_\" . $cart_product[\'product_id\'] . \"\\\" style=\\\"display: block;\\\">__N__                    <a class=\\\"cart-images\\\" href=\\\"product.php?id=\" . $cart_product[\'product_id\'] . \"\\\" title=\\\"\" . $cart_product[\'product_name\'] . \"\\\"><img src=\\\"\" . $cart_product[\'product_thumb\'] . \"\\\" alt=\\\"\" . $cart_product[\'product_name\'] . \"\\\"></a>__N__                    <div class=\\\"cart-info\\\">__N__                        <div class=\\\"product-name\\\"><span class=\\\"quantity-formated\\\"><span class=\\\"quantity\\\">\" . $cart_product[\'product_qty\'] . \"</span>&nbsp;x&nbsp;</span><a href=\\\"product.php?id=\" . $cart_product[\'product_id\'] . \"\\\" title=\\\"\" . $cart_product[\'product_name\'] . \"\\\" class=\\\"cart_block_product_name truncate3\\\">\" . $cart_product[\'product_name\'] . \"</a></div>__N__                        <span class=\\\"price\\\">\" . price($cart_product[\'product_price\']) . \" â¬</span>__N__                    </div>__N__                    <span class=\\\"remove_link\\\"><a rel=\\\"nofollow\\\" class=\\\"ajax_cart_block_remove_link\\\" href=\\\"\\\" data-code=\\\"\" . $cart_product[\'product_id\'] . \"\\\"> </a></span>__N__                </dt>__N__            </dl>\";__N____N__            $subtotal = ($cart_product[\'product_price\'] * $cart_product[\'product_qty\']);__N__            $total = ($total + $subtotal);__N__        }__N__        $cart_box .= \'__N__        <div class=\"cart-prices\">__N__            <div class=\"cart-prices-line first-line last-line\">__N__                <span class=\"price cart_block_total ajax_block_cart_total\">\' . $total . \' â¬</span>__N__                <span>\'.$weblangs->trans(\"Total\").\'</span>__N__            </div>__N__            <p> \'.$weblangs->trans(\"priceDependCountry\").\' </p>__N__        </div>__N__        \';__N__        die($cart_box); //exit and output content__N__    }else{__N__        die(\"<p class=\'cart_block_no_products\'> No products </p>\");