___N____N__// Remove item from shopping cart__N__if (GETPOST(\'remove_code\') && isset($_SESSION[\'products\'])) {__N__    $product_code   = GETPOST(\'remove_code\'); //get the product code to remove__N____N__    if(isset($_SESSION[\'products\'][$product_code]))__N__    {__N__        unset($_SESSION[\'products\'][$product_code]);__N__    }__N____N__    $total_items = count($_SESSION[\'products\']);__N__    die(json_encode(array(\'items\'=>$total_items)));__N__}__N____N__if (GETPOST(\'action\') && GETPOST(\'action\') == \"update_order\" && GETPOST(\'order\')) {__N__    include_once DOL_DOCUMENT_ROOT . \'/ecm/class/ecmfiles.class.php\';__N__    $error = 0;__N__    foreach (GETPOST(\'order\') as $id_image => $position_image) {__N__        $ecmfile = new EcmFiles($db);__N__        $result_get_ecmfile = $ecmfile->fetch($id_image);__N__        $ecmfile->position = $db->escape($position_image);__N__        $result_update_ecmfile = $ecmfile->update($user);__N__        if ($result_update_ecmfile < 0) {__N__		    $error++;__N__    	}__N__    }    __N__    if ($error > 0) {__N__        die(json_encode(array(\'success\' => false, \'message\' => $weblangs->trans(\'errorOccurred\'))));__N__	} else {__N__		die(json_encode(array(\'success\' => true, \'message\' => $weblangs->trans(\'orderChanged\'))));__N__	}__N____N__}__N____N__if ($_SERVER[\'REQUEST_METHOD\'] == \'POST\' && GETPOST(\'action\') == \'add_product_image\' && GETPOST(\'position\') && GETPOST(\'product_id\') && isset($_FILES[\'file\'][\'name\'])) {__N__    $error = 0;__N__    $error_messages = \'\';__N__    $product = new Product($db);__N__	$ret = $product->fetch(GETPOST(\'product_id\', \'int\'));__N__	if ($ret < 0) {__N__		$error++;__N__	}__N__    __N__    if ($error == 0) {__N__        $checkImage = checkImage($_FILES[\'file\']);__N__        if ($checkImage != false) {__N__            $error++;__N__    	    $error_messages = $checkImage;__N__        }__N__    }__N__    __N__    if ($error == 0){__N__        $upload_dir = $conf->product->multidir_output[1] . \'/\' . get_exdir(0, 0, 0, 1, $product, \'product\');__N__        $img = $upload_dir . \'/\' . $product->ref . \'-image_\' . GETPOST(\'position\', \'int\') . \'.jpg\';__N__        if (move_uploaded_file($_FILES[\'file\'][\'tmp_name\'], $img) != true){__N__            $error++;__N__            $error_messages = $weblangs->trans(\'fileCopyError\');__N__        } else {__N__            // Add thumb__N__            $product->addThumbs($img);__N____N__            // Add index to db__N__            $filename = basename($product->ref . \'-image_\' . GETPOST(\'position\', \'int\')  . \'.jpg\');__N__    		addFileIntoDatabaseIndex($upload_dir, $filename, \'MARKETPLACE\', \'uploaded\', 1, $product);__N__    __N__    		// Update index to put position__N__    		$rel_dir = preg_replace(\'/^\' . preg_quote(DOL_DATA_ROOT, \'/\') . \'/\', \'\', $upload_dir);__N__    		$rel_dir = preg_replace(\'/^[\\\\/]/\', \'\', $rel_dir);__N__    		$ref = dol_hash($rel_dir . \'/\' . $filename, 3);__N__    __N__    		$ecmfile = new EcmFiles($db);__N__    		$result_get_ecmfile = $ecmfile->fetch(\'\', $ref);__N__    __N__    		$ecmfile->position = GETPOST(\'position\', \'int\');__N__    		__N__    		$result_update_ecmfile = $ecmfile->update($user);__N__    __N__    		if ($result_update_ecmfile < 0) {__N__    			$error++;__N__    		    $error_messages = $weblangs->trans(\'fileCopyError\');__N__    	    }__N__    	}__N__    }__N__    __N__    if ($error > 0) {__N__        die(json_encode(array(\'success\' => false, \'message\' => $error_messages)));__N__	} else {__N__		die(json_encode(array(\'success\' => true, \'message\' => $weblangs->trans(\'imageAdded\'))));__N__	}__N__}__N____N__if (GETPOST(\'action\') == \"delete_image\" && GETPOST(\'id_image\')) {__N__    include_once DOL_DOCUMENT_ROOT . \'/ecm/class/ecmfiles.class.php\';__N__    $id_image = GETPOSTINT(\'id_image\');__N__    $error = 0;__N__    $ecmfile = new EcmFiles($db);__N__    $result_get_ecmfile = $ecmfile->fetch($id_image);__N__    $result_delete_ecmfile = $ecmfile->delete($user);__N__    if ($result_delete_ecmfile < 0) {__N__	    $error++;__N__	}__N__	__N__    if ($error > 0) {__N__        die(json_encode(array(\'success\' => false, \'message\' => $weblangs->trans(\'errorOccurred\'))));__N__	} else {__N__		die(json_encode(array(\'success\' => true, \'message\' => $weblangs->trans(\'imageDeleted\'))));__N__	}__N____N__}__N____N__// Generate CVS of sells history__N__if (GETPOST(\'sells_history\') && GETPOST(\'sells_history\') == 1) {__N____N__    // Get Account ID.__N__    include_once DOL_DOCUMENT_ROOT.\'/societe/class/societeaccount.class.php\';__N__    $user_id = $_SESSION[\'user\'][\'id_user\'];__N__    $customer_account = new SocieteAccount($db);__N__    $result = $customer_account->fetch($user_id);__N__    $customer_id = $customer_account->fk_soc;__N__    __N__    // Render a CSV of another customer__N__    if (!empty(GETPOST(\'id_customer\')) && GETPOST(\'id_customer\') != $customer_id) {__N__        // Only for members with a specific role__N__        require_once DOL_DOCUMENT_ROOT.\"/adherents/class/adherent.class.php\";__N__        $membre = new Adherent($db);__N__        $result = $membre->fetch(\'\', 0, $customer_id);__N__        __N__        $adminsCat = new Categorie($db);__N__    	$adminsCat->fetch(1);__N__    	__N__    	$result = $adminsCat->containsObject(\'member\', $membre->id);__N__    	if ($result > 0) {__N__            $customer_id = GETPOST(\'id_customer\');__N__    	}__N__    }__N____N____N__    // Get liste of products__N__    $products_ids = array();__N__    $sql = \'SELECT fk_product, MIN(datec) AS first_date FROM \'.MAIN_DB_PREFIX.\"product_fournisseur_price WHERE fk_soc = \".((int) $customer_id). \" GROUP BY fk_product ORDER BY first_date DESC\";__N__    if ($result_products = $db->query($sql)) {__N__    	while ($product_id = $result_products->fetch_object()) {__N__            $products_ids[] = $product_id->fk_product;__N__    	}__N__    }__N__    $products_ids_str = implode(\',\', $products_ids);__N__    __N____N__    /*$data_for_csv = \"SELECT s.nom as customer, c.date_commande as registred_on, sp.email, sc.label as country, c.date_commande, p.rowid as product_id ,p.label ,p.ref, d.total_ht as total_earned, d.total_ht as total_origin, c.fk_statut \";__N__    $data_for_csv .= \"FROM \".MAIN_DB_PREFIX.\"commande as c, \".MAIN_DB_PREFIX.\"commandedet as d , \".MAIN_DB_PREFIX.\"product as p , \".MAIN_DB_PREFIX.\"societe as s , \".MAIN_DB_PREFIX.\"socpeople as sp , \".MAIN_DB_PREFIX.\"c_country as sc \";__N__    $data_for_csv .= \"WHERE p.rowid = d.fk_product AND s.rowid = c.fk_soc AND sp.fk_soc = s.rowid AND sp.fk_pays = sc.rowid AND c.rowid = d.fk_commande and d.fk_product IN (\" . $products_ids_str . \") and d.total_ht != 0 and c.fk_statut IN (1,3) and (c.facture = 1 || c.ref_ext IS NOT NULL) and c.module_source = \'Marketplace\' \";__N__    $data_for_csv .= \"ORDER BY c.date_commande DESC\";*/__N__    __N__    $data_for_csv = \"SELECT c.rowid as c_id, d.rowid as d_id, s.nom as customer, s.datec as registred_on, sp.email, sc.label as country, c.date_commande, \";__N__    $data_for_csv .= \"p.rowid as product_id, p.label, p.ref, d.total_ht as total_earned, d.total_ht as total_origin, c.fk_statut \";__N__    $data_for_csv .= \"FROM \".MAIN_DB_PREFIX.\"commande as c \";__N__    $data_for_csv .= \"JOIN \".MAIN_DB_PREFIX.\"commandedet as d ON c.rowid = d.fk_commande \";__N__    $data_for_csv .= \"JOIN \".MAIN_DB_PREFIX.\"product as p ON p.rowid = d.fk_product \";__N__    $data_for_csv .= \"JOIN \".MAIN_DB_PREFIX.\"societe as s ON s.rowid = c.fk_soc \";__N__    $data_for_csv .= \"JOIN \".MAIN_DB_PREFIX.\"societe_account as sa ON s.rowid = sa.fk_soc \";__N__    $data_for_csv .= \"JOIN \".MAIN_DB_PREFIX.\"socpeople as sp ON sp.fk_soc = s.rowid  AND sp.ref_ext = sa.rowid \";__N__    $data_for_csv .= \"JOIN \".MAIN_DB_PREFIX.\"c_country as sc ON sp.fk_pays = sc.rowid \";__N__    $data_for_csv .= \"WHERE d.fk_product IN (\" . $products_ids_str . \") \";__N__    $data_for_csv .= \"AND d.total_ht != 0 \";__N__    $data_for_csv .= \"AND c.fk_statut IN (1,3) \";__N__    $data_for_csv .= \"AND (c.facture = 1 OR c.ref_ext IS NOT NULL) \";__N__    $data_for_csv .= \"AND c.module_source = \'Marketplace\' \";__N__    $data_for_csv .= \"ORDER BY c.date_commande DESC\";__N__    //print $data_for_csv;__N__    __N__    //print $data_for_csv;__N____N__    if ($result_data_for_csv = $db->query($data_for_csv)) {__N__        $row_no = 1;__N__        $data = [__N__            [\'#\', \'Customer\', \'Customer date creation\', \'Customer email\', \'Customer country\', \'Date sell\', \'Product id\', \'Product label\', \'Product ref\', \'Amount earned\', \'Amount origin\', \'Note\']__N__        ];__N__        $current_date = new DateTime();__N__        while ($row = $result_data_for_csv->fetch_object()) {__N____N__            $date_commande = new DateTime($row->date_commande);__N__        __N__            $interval = $current_date->diff($date_commande);__N__            $days_diff = $interval->days;__N__            __N__            if ($days_diff > 30) {__N__                $status = \'validated\';__N__            } else {__N__                $status = \'\';__N__            }__N__            __N__            $data[] = [__N__                $row_no++,__N__                $row->customer,__N__                $row->registred_on,__N__                $row->email,__N__                $row->country,__N__                $row->date_commande,__N__                $row->product_id,__N__                $row->label,__N__                $row->ref,__N__                /*price($row->total_earned),__N__                price($row->total_origin),*/__N__                number_format($row->total_earned, 2, \'.\', \'\'),__N__                number_format($row->total_origin, 2, \'.\', \'\'),__N__                $status__N__            ];__N__        }__N__    }__N____N__    $filepath = \'export_\' . date(\'Ymd\') . \'.csv\';__N____N__    $fp = fopen($filepath, \'w\');__N____N__    foreach ($data as $row) {__N__        fputcsv($fp, $row, \';\');__N__    }__N____N__    fclose($fp);__N__    __N__    __N__    print json_encode([\'filepath\' => $filepath]);__N__    exit();__N__}__N____N____N__if (GETPOST(\'filepath\') && GETPOST(\'action\') === \'remove_csv\') {__N__    $filepath = GETPOST(\'filepath\');__N__    if (file_exists($filepath)) {__N__        unlink($filepath);__N__        echo json_encode([\'status\' => \'success\', \'message\' => \'File deleted\']);__N__    } else {__N__        echo json_encode([\'status\' => \'error\', \'message\' => \'file not found\']);__N__    }__N__}__N____N____N____N____N__?>__N__', '', 0);


